const renderItems=collection=>{const collectionList=document.getElementById("collection");collection.forEach(item=>{const listItem=document.createElement("li"),itemTitle=document.createElement("h2");itemTitle.innerHTML=item.title,listItem.appendChild(itemTitle);const itemImage=document.createElement("img");itemImage.src=item.posterImage,listItem.appendChild(itemImage);const itemDetails=`\n\t\t\t\t<p>📍 Built in <span>${item.location}</span></p>\n\t\t\t\t<p>✍️ Designed by ${item.architect}</p>\n\t\t\t\t<details>\n\t\t\t\t\t<summary>Read more</summary>\n\t\t\t\t\t<p>${item.description}</p>\n\t\t\t\t</details>\n\t\t\t`;listItem.insertAdjacentHTML("beforeend",itemDetails),collectionList.appendChild(listItem)})};fetch("assets/collection.json").then(response=>response.json()).then(collection=>{renderItems(collection)});