const createCollectionHTML=data=>{const collectionGrid=document.getElementById("collection");data.forEach(item=>{console.log(item);const listItemHTML=`\n            <li>\n                <p class="item-title">${item.name}</p>\n                <p class="item-year">${item.year}</p>\n                <p class="item-description">${item.description}</p>\n                <img src="${item.image}" alt="${item.name}">\n            </li>\n        `;collectionGrid.insertAdjacentHTML("beforeend",listItemHTML)})};fetch("collection.json").then(response=>response.json()).then(data=>{createCollectionHTML(data)});