const rappers=document.querySelectorAll(".grid-item");rappers.forEach(rapper=>{rapper.addEventListener("click",()=>{const audio=rapper.querySelector("audio");audio.play(),rapper.classList.add("jiggle"),setTimeout(()=>{rapper.classList.remove("jiggle")},1200)})});const filterButtons=document.querySelectorAll(".filter-button"),toggleRapper=(elements,showHide)=>{elements.forEach(el=>{"show"==showHide?el.classList.remove("is-hidden"):el.classList.add("is-hidden")})};filterButtons.forEach(button=>{button.addEventListener("click",()=>{const filterValue=button.getAttribute("data-filter"),matchingRappers=document.querySelectorAll(`.grid-item[data-filter="${filterValue}"]`);toggleRapper(rappers,"hide"),toggleRapper(matchingRappers,"show"),"All"==button.getAttribute("data-filter")&&toggleRapper(rappers,"show")})});